// OtherScreens.jsx
import React from "react";

function OtherScreens({ stepIndex, profileData, casualData, interviewData }) {
  // ステップインデックスに基づいて表示する情報を決定
  const getDisplayedInfo = () => {
    const info = [];

    // 調整中ステップ1以降であれば、エントリー情報を表示
    if (stepIndex >= 1) {
      info.push(
        { label: "氏名", value: profileData.name || "未入力" },
        { label: "フリガナ", value: profileData.furigana || "未入力" }
      );
    }

    // 調整中ステップ3以降であれば、カジュアル情報を追加
    if (stepIndex >= 3) {
      info.push(
        { label: "判定結果", value: casualData.result || "未入力" },
        { label: "誠実", value: casualData.honesty || "未入力" },
        { label: "チーム愛", value: casualData.teamLove || "未入力" },
        { label: "愛嬌", value: casualData.charm || "未入力" },
        { label: "謙虚さ", value: casualData.modesty || "未入力" },
        { label: "責任感", value: casualData.responsibility || "未入力" }
      );
    }

    // 調整中ステップ5以降であれば、1次面接情報を追加
    if (stepIndex >= 5) {
      info.push(
        { label: "1次面接 - 専門知識・技術力", value: interviewData.firstInterview.technicalSkills || "未入力" },
        { label: "1次面接 - 問題解決能力", value: interviewData.firstInterview.problemSolving || "未入力" },
        { label: "1次面接 - 論理的思考力", value: interviewData.firstInterview.logicalThinking || "未入力" },
        { label: "1次面接 - リーダーシップ・主体性", value: interviewData.firstInterview.leadership || "未入力" },
        { label: "1次面接 - キャリアビジョン", value: interviewData.firstInterview.careerVision || "未入力" }
      );
    }

    // 調整中ステップ7以降であれば、2次面接情報を追加
    if (stepIndex >= 7) {
      info.push(
        { label: "2次面接 - 専門知識・技術力", value: interviewData.secondInterview.technicalSkills || "未入力" },
        { label: "2次面接 - 問題解決能力", value: interviewData.secondInterview.problemSolving || "未入力" },
        { label: "2次面接 - 論理的思考力", value: interviewData.secondInterview.logicalThinking || "未入力" },
        { label: "2次面接 - リーダーシップ・主体性", value: interviewData.secondInterview.leadership || "未入力" },
        { label: "2次面接 - キャリアビジョン", value: interviewData.secondInterview.careerVision || "未入力" }
      );
    }

    // 調整中ステップ9以降であれば、最終面接情報を追加
    if (stepIndex >= 9) {
      info.push(
        { label: "最終面接 - 専門知識・技術力", value: interviewData.finalInterview.technicalSkills || "未入力" },
        { label: "最終面接 - 問題解決能力", value: interviewData.finalInterview.problemSolving || "未入力" },
        { label: "最終面接 - 論理的思考力", value: interviewData.finalInterview.logicalThinking || "未入力" },
        { label: "最終面接 - リーダーシップ・主体性", value: interviewData.finalInterview.leadership || "未入力" },
        { label: "最終面接 - キャリアビジョン", value: interviewData.finalInterview.careerVision || "未入力" }
      );
    }

    return info;
  };

  const displayedInfo = getDisplayedInfo();

  return (
    <div className="container">
      <h2>調整中</h2>
      <div className="info-section">
        {displayedInfo.map((item, index) => (
          <p key={index}>
            <strong>{item.label}:</strong> {item.value}
          </p>
        ))}
      </div>
    </div>
  );
}

export default OtherScreens;
