import React, { useRef } from "react";
import { TextField, Button, Card, CardContent, Typography, Box, Grid, Container, Divider } from "@mui/material";

function ProfileScreen({ profileData, setProfileData }) {
  const fileInputRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileUpload = (e) => {
    const { name, files } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: files[0] }));
  };

  const photoPreviewUrl = profileData.photo ? URL.createObjectURL(profileData.photo) : null;

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h5" gutterBottom>
        個人プロフィール
      </Typography>
      <Divider sx={{ mb: 3 }} />
      <Grid container spacing={4}>
        {/* 左側セクション */}
        <Grid item xs={12} md={4}>
          <Card sx={{ p: 3, boxShadow: 3 }}>
          <Box
          sx={{
            display: "flex",
            alignItems: "flex-end", // 下揃え
            gap: 2 // 写真と名前の間のスペース
          }}
        ></Box>
            <Box
              sx={{
                width: 120,
                height: 160,
                backgroundColor: "#f5f5f5",
                borderRadius: "8px",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                backgroundImage: photoPreviewUrl ? `url(${photoPreviewUrl})` : "none",
                backgroundSize: "cover",
                backgroundPosition: "center",
                border: "2px solid #ddd",
                cursor: "pointer",
                // mx: "auto",
              }}
              onClick={() => fileInputRef.current.click()}
            >
              {!photoPreviewUrl && <Typography variant="body2">写真未挿入</Typography>}
            </Box>
            <input
              type="file"
              name="photo"
              accept="image/*"
              ref={fileInputRef}
              style={{ display: "none" }}
              onChange={handleFileUpload}
            />
            <Typography variant="body2" align="center" color="text.secondary">
            {profileData.furigana || "やまだ たろう"}
            </Typography>
            <Typography variant="h6" align="center" sx={{ mt: 2 }}>
              {profileData.name || "山田 太郎"}
            </Typography>
          </Card>
        </Grid>

        {/* 右側セクション */}
        <Grid item xs={12} md={8}>
          <Card sx={{ p: 3, boxShadow: 3 }}>
            <CardContent>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="職務経歴"
                    name="career"
                    value={profileData.career || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Button variant="contained" component="label" fullWidth>
                    履歴書
                    <input type="file" name="resume" hidden onChange={handleFileUpload} />
                  </Button>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Button variant="contained" component="label" fullWidth>
                    職務経歴書
                    <input type="file" name="careerSheet" hidden onChange={handleFileUpload} />
                  </Button>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="経路"
                    name="route"
                    value={profileData.route || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="生年月日"
                    name="birthdate"
                    value={profileData.birthdate || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="住所"
                    name="address"
                    value={profileData.address || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="出身"
                    name="origin"
                    value={profileData.origin || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="就業状況"
                    name="employmentStatus"
                    value={profileData.employmentStatus || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="大学"
                    name="university"
                    value={profileData.university || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="高校"
                    name="highSchool"
                    value={profileData.highSchool || ""}
                    onChange={handleInputChange}
                    variant="outlined"
                  />
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

export default ProfileScreen;
