import React from "react";

function ProfileScreen({ profileData, setProfileData }) {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileUpload = (e) => {
    const { name, files } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: files[0] }));
  };

  const photoPreviewUrl = profileData.photo
    ? URL.createObjectURL(profileData.photo)
    : null;

  return (
    <div className="container">
      <h2>エントリー</h2>
      <div className="flex-row">
        {/* 写真のプレビュー */}
        <div
          className="profile-photo"
          style={{
            backgroundImage: photoPreviewUrl ? `url(${photoPreviewUrl})` : "none",
          }}
        >
          {!photoPreviewUrl && <p>写真未挿入</p>}
        </div>

        {/* 名前とフリガナ */}
        <div className="info-section">
          <div className="info-item">
            <label>フリガナ:</label>
            <input
              type="text"
              name="furigana"
              value={profileData.furigana || ""}
              onChange={handleInputChange}
            />
          </div>
          <div className="info-item">
            <label>名前:</label>
            <input
              type="text"
              name="name"
              value={profileData.name || ""}
              onChange={handleInputChange}
            />
          </div>
        </div>
      </div>

      {/* 最終学歴と職務経歴 */}
      <div className="info-section">
        <div className="info-item">
          <label>最終学歴:</label>
          <input
            type="text"
            name="education"
            value={profileData.education || ""}
            onChange={handleInputChange}
          />
        </div>
        <div className="info-item">
          <label>職務経歴:</label>
          <input
            type="text"
            name="career"
            value={profileData.career || ""}
            onChange={handleInputChange}
          />
        </div>
      </div>

      {/* ファイルアップロード */}
      <div className="info-section">
        <div className="info-item">
          <label>履歴書:</label>
          <input
            type="file"
            name="resume"
            onChange={handleFileUpload}
          />
        </div>
        <div className="info-item">
          <label>職務経歴書:</label>
          <input
            type="file"
            name="careerSheet"
            onChange={handleFileUpload}
          />
        </div>
      </div>
    </div>
  );
}








export default ProfileScreen;
