import React, { useRef } from "react";

function ProfileScreen({ profileData, setProfileData }) {
  const fileInputRef = useRef(null); // ファイル入力フィールドを参照

  const handleFileUpload = (e) => {
    const { files } = e.target;
    if (files && files[0]) {
      setProfileData((prev) => ({ ...prev, photo: files[0] }));
    }
  };

  const photoPreviewUrl = profileData.photo
    ? URL.createObjectURL(profileData.photo)
    : null;

  return (
    <div className="container">
      <h2>エントリー</h2>
      <div style={{ display: "flex", alignItems: "center", marginBottom: "20px" }}>
        {/* 写真アップロードとプレビュー */}
        <div
          style={{
            width: "120px", // 横幅
            height: "160px", // 縦幅 (4:3比率)
            backgroundColor: "#e0e0e0",
            borderRadius: "5px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundImage: photoPreviewUrl ? `url(${photoPreviewUrl})` : "none",
            backgroundSize: "cover", // 枠いっぱいに画像を表示
            backgroundPosition: "center",
            border: "1px solid #ccc",
            marginRight: "20px",
            cursor: "pointer",
          }}
          onClick={() => fileInputRef.current.click()} // 四角形をクリックするとファイル選択
        >
          {!photoPreviewUrl && <p>写真未挿入</p>}
        </div>
        {/* 隠されたファイル入力フィールド */}
        <input
          type="file"
          accept="image/*"
          ref={fileInputRef}
          style={{ display: "none" }}
          onChange={handleFileUpload}
        />
      </div>

      {/* フリガナと名前入力 */}
      <div style={{ marginBottom: "20px" }}>
        <div>
          <label>フリガナ:</label>
          <input
            type="text"
            name="furigana"
            value={profileData.furigana || ""}
            onChange={(e) =>
              setProfileData((prev) => ({ ...prev, furigana: e.target.value }))
            }
            style={{ width: "100%" }}
          />
        </div>
        <div>
          <label>名前:</label>
          <input
            type="text"
            name="name"
            value={profileData.name || ""}
            onChange={(e) =>
              setProfileData((prev) => ({ ...prev, name: e.target.value }))
            }
            style={{ width: "100%" }}
          />
        </div>
      </div>
    </div>
  );
}

export default ProfileScreen;
