import React, { useState } from "react";
import axios from "axios"; // APIと通信するためのライブラリ

const ProfileForm = () => {
  const [form, setForm] = useState({
    name: "", // 氏名
    age: "", // 年齢
    education: "", // 学歴
    career: "", // 職務経歴
  });
  const [profileId, setProfileId] = useState(null); // 作成されたプロフィールID

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value }); // 入力フォームの値を更新
  };

  const handleCreateProfile = async () => {
    try {
      const response = await axios.post("http://localhost:3001/api/profiles", form);
      setProfileId(response.data.id); // 作成されたIDを保存
      alert("プロフィールを作成しました！");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2>プロフィール作成</h2>
      <input type="text" name="name" placeholder="氏名" onChange={handleInputChange} />
      <input type="number" name="age" placeholder="年齢" onChange={handleInputChange} />
      <input type="text" name="education" placeholder="学歴" onChange={handleInputChange} />
      <input type="text" name="career" placeholder="職務経歴" onChange={handleInputChange} />
      <button onClick={handleCreateProfile}>プロフィール作成</button>
    </div>
  );
};

export default ProfileForm;
