import React, { useRef } from "react";
import { TextField, Button, Card, CardContent, Typography, Box, Grid, Container } from "@mui/material";

function ProfileScreen({ profileData, setProfileData }) {
  const fileInputRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileUpload = (e) => {
    const { name, files } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: files[0] }));
  };

  const photoPreviewUrl = profileData.photo ? URL.createObjectURL(profileData.photo) : null;

  return (
    <Container maxWidth="sm" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" align="center" gutterBottom>
        エントリー
      </Typography>
      <Card sx={{ p: 3, boxShadow: 3 }}>
        <CardContent>
          <Grid container spacing={3} alignItems="center">
            {/* 写真アップロードとプレビュー */}
            <Grid item xs={12} display="flex" justifyContent="center">
              <Box
                sx={{
                  width: 140,
                  height: 180,
                  backgroundColor: "#f5f5f5",
                  borderRadius: "8px",
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  backgroundImage: photoPreviewUrl ? `url(${photoPreviewUrl})` : "none",
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                  border: "2px solid #ddd",
                  cursor: "pointer",
                  boxShadow: 1
                }}
                onClick={() => fileInputRef.current.click()}
              >
                {!photoPreviewUrl && <Typography variant="body2">写真未挿入</Typography>}
              </Box>
              <input
                type="file"
                name="photo"
                accept="image/*"
                ref={fileInputRef}
                style={{ display: "none" }}
                onChange={handleFileUpload}
              />
            </Grid>

            {/* 入力フィールド */}
            <Grid item xs={12}>
              <TextField fullWidth label="フリガナ" name="furigana" value={profileData.furigana || ""} onChange={handleInputChange} variant="outlined" />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="名前" name="name" value={profileData.name || ""} onChange={handleInputChange} variant="outlined" />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="最終学歴" name="education" value={profileData.education || ""} onChange={handleInputChange} variant="outlined" />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="職務経歴" name="career" value={profileData.career || ""} onChange={handleInputChange} variant="outlined" />
            </Grid>

            {/* ファイルアップロード */}
            <Grid item xs={12}>
              <Button variant="contained" component="label" fullWidth sx={{ bgcolor: "primary.main", color: "white" }}>
                履歴書をアップロード
                <input type="file" name="resume" hidden onChange={handleFileUpload} />
              </Button>
            </Grid>
            <Grid item xs={12}>
              <Button variant="contained" component="label" fullWidth sx={{ bgcolor: "secondary.main", color: "white" }}>
                職務経歴書をアップロード
                <input type="file" name="careerSheet" hidden onChange={handleFileUpload} />
              </Button>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Container>
  );
}

export default ProfileScreen;
