import React, { useState } from "react";

function ProfileScreen({ profileData, setProfileData }) {
  const [currentStep, setCurrentStep] = useState(0);

  const steps = [
    "エントリー",
    "調整中",
    "カジュアル",
    "調整中",
    "1次",
    "調整中",
    "2次",
    "調整中",
    "最終",
    "調整中",
  ];

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep((prev) => prev + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep((prev) => prev - 1);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: files[0] }));
  };

  const photoPreviewUrl = profileData.photo
    ? URL.createObjectURL(profileData.photo)
    : null;

  return (
    <div className="container">
      {/* 名前と写真 */}
      <div className="flex-row">
        {/* 証明写真 */}
        <div
          className="profile-photo"
          style={{
            backgroundImage: photoPreviewUrl ? `url(${photoPreviewUrl})` : "none",
          }}
        >
          {!photoPreviewUrl && <p>写真を挿入</p>}
          <input
            type="file"
            accept="image/*"
            name="photo"
            onChange={(e) => handleFileChange({ ...e, name: "photo" })}
          />
        </div>
        {/* 名前入力 */}
        <div>
          <div>
            <input
              type="text"
              name="furigana"
              value={profileData.furigana || ""}
              onChange={handleInputChange}
              placeholder="フリガナを入力"
              style={{
                fontSize: "1.2rem",
                fontWeight: "normal",
                border: "none",
                borderBottom: "2px solid #ccc",
                marginBottom: "10px",
                outline: "none",
                width: "200px",
              }}
            />
          </div>
          <div>
            <input
              type="text"
              name="name"
              value={profileData.name || ""}
              onChange={handleInputChange}
              placeholder="漢字を入力"
              style={{
                fontSize: "1.5rem",
                fontWeight: "bold",
                border: "none",
                borderBottom: "2px solid #ccc",
                marginBottom: "10px",
                outline: "none",
                width: "200px",
              }}
            />
          </div>
        </div>
      </div>

      {/* ステップ */}
      <div className="step-container">
        {steps.map((step, index) => (
          <div
            key={index}
            className={`step ${index === currentStep ? "highlight" : ""}`}
          >
            {step}
          </div>
        ))}
      </div>

      {/* 最終学歴、職務経歴、ファイル添付 */}
      <div className="info-section">
        <div>
          <label>最終学歴: </label>
          <input
            type="text"
            name="education"
            value={profileData.education}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>職務経歴: </label>
          <input
            type="text"
            name="career"
            value={profileData.career}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>履歴書: </label>
          <input
            type="file"
            name="resume"
            onChange={handleFileChange}
          />
        </div>
        <div>
          <label>職務経歴書: </label>
          <input
            type="file"
            name="careerSheet"
            onChange={handleFileChange}
          />
        </div>
      </div>

      {/* 次へ/前へボタン */}
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
        <button
          onClick={handlePrevious}
          disabled={currentStep === 0}
          style={{
            padding: "10px 20px",
            backgroundColor: currentStep === 0 ? "#ccc" : "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: currentStep === 0 ? "not-allowed" : "pointer",
          }}
        >
          前へ
        </button>
        <button
          onClick={handleNext}
          disabled={currentStep === steps.length - 1}
          style={{
            padding: "10px 20px",
            backgroundColor: currentStep === steps.length - 1 ? "#ccc" : "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: currentStep === steps.length - 1 ? "not-allowed" : "pointer",
          }}
        >
          次へ
        </button>
      </div>
    </div>
  );
}

export default ProfileScreen;
