import React from "react";

function SecondInterviewScreen({ interviewData, setInterviewData }) {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInterviewData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="container">
      <h2>二次面接</h2>
      <div>
        <label>専門知識・技術力:</label>
        <input
          type="text"
          name="technicalSkills"
          value={interviewData.technicalSkills || ""}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>問題解決能力:</label>
        <input
          type="text"
          name="problemSolving"
          value={interviewData.problemSolving || ""}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>論理的思考力:</label>
        <input
          type="text"
          name="logicalThinking"
          value={interviewData.logicalThinking || ""}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>リーダーシップ・主体性:</label>
        <input
          type="text"
          name="leadership"
          value={interviewData.leadership || ""}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>キャリアビジョン:</label>
        <input
          type="text"
          name="careerVision"
          value={interviewData.careerVision || ""}
          onChange={handleInputChange}
        />
      </div>
    </div>
  );
}

export default SecondInterviewScreen;
