import React, { useContext } from "react";
import { DataContext } from "../context/DataContext";

function InterviewScreen() {
  const { profileData } = useContext(DataContext);

  return (
    <div className="container">
      <h2>面談結果入力</h2>
      <div>
        <p>最終学歴: {profileData.education}</p>
        <p>職務経歴: {profileData.career}</p>
        <p>
          履歴書:{" "}
          {profileData.resumeFile ? profileData.resumeFile.name : "未添付"}
        </p>
        <p>
          職務経歴書:{" "}
          {profileData.careerSheetFile
            ? profileData.careerSheetFile.name
            : "未添付"}
        </p>
      </div>
    </div>
  );
}

export default InterviewScreen;
