import React from "react";

function CasualScreen({ casualData, setCasualData, onPrevious, onNext, isFirstStep, isLastStep }) {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCasualData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="container">
      {/* ヘッダー */}
      <div className="flex-row">
        <div className="profile-image" />
        <div>
          <h2>やまだ たろう</h2>
          <h2>山田 太郎 (30)</h2>
        </div>
      </div>

      {/* 判定結果 */}
      <div className="info-section">
        <div>
          <label>判定結果:</label>
          <input
            type="text"
            name="result"
            value={casualData.result || ""}
            onChange={handleInputChange}
            style={{ width: "100%", marginBottom: "10px" }}
          />
        </div>
        {/* 他の入力フィールド */}
      </div>

      {/* 前へ・次へボタン */}
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
        <button
          onClick={onPrevious}
          disabled={isFirstStep}
          style={{
            padding: "10px 20px",
            backgroundColor: isFirstStep ? "#ccc" : "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: isFirstStep ? "not-allowed" : "pointer",
          }}
        >
          前へ
        </button>
        <button
          onClick={onNext}
          disabled={isLastStep}
          style={{
            padding: "10px 20px",
            backgroundColor: isLastStep ? "#ccc" : "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: isLastStep ? "not-allowed" : "pointer",
          }}
        >
          次へ
        </button>
      </div>
    </div>
  );
}

export default CasualScreen;
